import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import fakeData from './now_playing.json';
import { Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button } from 'reactstrap';

class App extends Component {
  render() {
    const moviesList = fakeData.results;
    return (
      <div className="App">
        {
          moviesList.map(movie => {
            return(
              <Movie 
                title={movie.title}
                overview={movie.overview}
                id={movie.id}
              />
            )
          })
        }
      </div>
    );
  }
}
class Movie extends Component{
  render(){
    const moviePath = "https://image.tmdb.org/t/p/w342" + this.props.poster_path;
    return (
      <Card id={this.props.id}>
        <CardImg top width="100%" height="100" src={moviePath} alt="Card image cap" />
        <CardBody>
          <CardTitle>{this.props.title}</CardTitle>
          <CardSubtitle>{this.props.overview}</CardSubtitle>
        </CardBody>
      </Card>
    )
  }
}
export default App;
